import { Box, Heading, ResponsiveContext, Text } from 'grommet'
import React, { useContext } from 'react'
import styled from 'styled-components'
import { UserProfile } from '../../types'
import PictureProfile from '../PictureProfile'
import Social from '../Social'

const Label = styled.div`
    color: #777777;
    box-shadow: rgb(217, 217, 217) 0px 0px 0px 1px;
    border-radius: 40px;
    padding: 0px 8px 0px 8px;
    font-weight: 500;
    margin: 0px;
    font-size: 12px;
    height: fit-content;
    width: fit-content;
`

const ProfileDescription: React.FC<UserProfile> = props => {
    const size = useContext(ResponsiveContext)
    const isMobile = size === 'small'

    const Biography = (
        <Box width="large" margin={{ bottom: isMobile ? 'medium' : 'none' }}>
            <Text size={isMobile ? 'small' : 'medium'}>{props.biography}</Text>
        </Box>
    )

    return (
        <Box pad={{ horizontal: 'large' }}>
            <Box direction="row" justify="start" margin={{ bottom: 'large' }}>
                <Box
                    margin={{ right: 'large' }}
                    flex={false}
                    responsive={false}
                >
                    <PictureProfile size={size} imageUrl={props.pictureUrl} />
                </Box>
                <Box width="full">
                    <Box direction="row" align="start" justify="between">
                        <Box direction="row-responsive">
                            <Heading level={1} size="large">
                                {props.firstname}
                            </Heading>
                            {props.label && <Label>{props.label}</Label>}
                        </Box>
                        <Social {...props.social} />
                    </Box>
                    {!isMobile && Biography}
                </Box>
            </Box>
            {isMobile && Biography}
        </Box>
    )
}

export default ProfileDescription
